import React from "react";
import { Metadata } from "next";
import { AbstractFactoryDemo } from "@/components/features/abstract-factory/AbstractFactoryDemo";
import { CodeTabs } from "@/components/shared/CodeTabs";
import { patterns } from "@/lib/registry";
import { prosConsData } from "@/lib/pattern-content";
import { PatternHeader } from "@/components/shared/pattern-layout/PatternHeader";
import { PatternSection } from "@/components/shared/pattern-layout/PatternSection";
import { ProsConsList } from "@/components/shared/pattern-layout/ProsConsList";

export const dynamic = "force-static";
export const metadata: Metadata = { title: "Abstract Factory Pattern", description: "Learn the Abstract Factory Pattern." };
const pattern = patterns.find((p) => p.slug === "abstract-factory");
const content = prosConsData["abstract-factory"];

export default function AbstractFactoryPage() {
    if (!pattern) return <div>Pattern not found</div>;

    return (
        <div className="max-w-4xl mx-auto px-6 py-12 space-y-12">
            <PatternHeader
                title={pattern.title}
                description={pattern.description}
                category={pattern.category}
                tags={pattern.tags}
            />

            <PatternSection title="The Problem">
                <p>
                    Imagine you are building a furniture shop simulator. Your code creates furniture objects like <code>Chair</code>, <code>Sofa</code>, and <code>CoffeeTable</code>.
                </p>
                <p>
                    However, your furniture comes in families: <strong>Modern</strong>, <strong>Victorian</strong>, and <strong>ArtDeco</strong>. You need a way to create individual furniture objects so that they match other objects of the same family. A customer doesn't want a Modern Sofa with Victorian Chairs.
                </p>
                <p>
                    Also, you don't want to change existing code when adding new products or families of products.
                </p>
            </PatternSection>

            <PatternSection title="The Solution">
                <p>
                    The <strong>Abstract Factory</strong> pattern suggests that you explicitly declare interfaces for each distinct product of the product family (e.g., Chair, Sofa). Then you can make all variants of products follow those interfaces.
                </p>
                <p>
                    Next, you declare the <em>Abstract Factory</em>‚Äîan interface with a list of creation methods for all products that are part of the product family (e.g., <code>createChair</code>, <code>createSofa</code>). These methods return abstract product types.
                </p>
            </PatternSection>

            <PatternSection title="Interactive Playground">
                <AbstractFactoryDemo />
            </PatternSection>

            <PatternSection title="Implementation Details">
                <CodeTabs tabs={[{
                    id: "ts", label: "Example", language: "typescript", code: `interface GUIFactory {
  createButton(): Button;
  createCheckbox(): Checkbox;
}

class WinFactory implements GUIFactory {
  createButton(): Button { return new WinButton(); }
  createCheckbox(): Checkbox { return new WinCheckbox(); }
}

class MacFactory implements GUIFactory {
  createButton(): Button { return new MacButton(); }
  createCheckbox(): Checkbox { return new MacCheckbox(); }
}

// Client code works with factories and products only through abstract types
function createUI(factory: GUIFactory) {
  const button = factory.createButton();
  const checkbox = factory.createCheckbox();
}
` }]} />
            </PatternSection>

            {content && (
                <PatternSection title="Pros & Cons">
                    <ProsConsList pros={content.pros} cons={content.cons} />
                </PatternSection>
            )}
        </div>
    );
}

import React from "react";
import { Metadata } from "next";
import { AdapterDemo } from "@/components/features/adapter/AdapterDemo";
import { CodeTabs } from "@/components/shared/CodeTabs";
import { patterns } from "@/lib/registry";
import { prosConsData } from "@/lib/pattern-content";
import { ArrowRight, Box, Layout, TestTube } from "lucide-react";
import { PatternHeader } from "@/components/shared/pattern-layout/PatternHeader";
import { PatternSection } from "@/components/shared/pattern-layout/PatternSection";
import { ProsConsList } from "@/components/shared/pattern-layout/ProsConsList";

export const dynamic = "force-static";

export const metadata: Metadata = {
    title: "Adapter Pattern | Design Pattern Tutorial",
    description: "Learn the Adapter Design Pattern with interactive demos.",
};

const pattern = patterns.find((p) => p.slug === "adapter");
const content = prosConsData["adapter"];

export default function AdapterPage() {
    if (!pattern) return <div>Pattern not found</div>;

    return (
        <div className="max-w-4xl mx-auto px-6 py-12 space-y-12">
            <PatternHeader
                title={pattern.title}
                description={pattern.description}
                category={pattern.category}
                tags={pattern.tags}
            />

            {/* Hook */}
            <section aria-label="Concept Visualization" className="flex justify-center py-8">
                <div className="flex items-center gap-4 text-slate-400">
                    <div className="flex flex-col items-center gap-2">
                        <div className="w-16 h-16 bg-amber-100 rounded-md border-2 border-amber-300 flex items-center justify-center font-mono text-xs text-amber-700 font-bold transform -rotate-3">
                            &lt;XML/&gt;
                        </div>
                        <span className="text-xs font-bold">Incompatible</span>
                    </div>
                    <ArrowRight className="w-6 h-6 animate-pulse" />
                    <div className="relative group">
                        <div className="w-16 h-16 bg-purple-600 rounded-full flex items-center justify-center text-white shadow-lg z-10 relative">
                            <Box className="w-8 h-8" />
                        </div>
                        <div className="absolute inset-0 bg-purple-400 rounded-full blur-lg opacity-20 group-hover:opacity-40 transition-opacity" />
                        <span className="absolute -bottom-6 left-1/2 -translate-x-1/2 text-xs font-bold text-purple-600 whitespace-nowrap">Adapter</span>
                    </div>
                    <ArrowRight className="w-6 h-6 animate-pulse" />
                    <div className="flex flex-col items-center gap-2">
                        <div className="w-16 h-16 bg-blue-50 rounded-xl border border-blue-200 flex items-center justify-center font-mono text-xs text-blue-700 font-bold shadow-sm">
                            &#123;JSON&#125;
                        </div>
                        <span className="text-xs font-bold">Standard</span>
                    </div>
                </div>
            </section>

            <PatternSection title="The Problem">
                <p>
                    Imagine you are building a modern stock market dashboard. Your UI components expect
                    data in a clean JSON format. However, you need to display data from a legacy banking
                    system that only outputs XML.
                </p>
                <p>
                    You can't change the legacy system (it's 20 years old), and you shouldn't change your
                    UI components to parse XML (that breaks clean architecture).
                </p>
                <div className="bg-rose-50 border border-rose-100 rounded-lg p-4 font-mono text-sm text-rose-800 mt-4">
                    <p className="font-bold mb-2">‚ùå Bad Approach: Polluting the UI</p>
                    <pre className="whitespace-pre-wrap">
                        {`// Don't do this inside your React component!
const StockChart = ({ data }) => {
  if (data.startsWith("<xml>")) {
    const xml = parseXML(data); // ü§¢ Logic coupling
    return <BarChart values={xml.values} />;
  }
  return <BarChart values={data.values} />;
}`}
                    </pre>
                </div>
            </PatternSection>

            <PatternSection title="The Solution">
                <p>
                    Create a separate class (or function) called an <strong>Adapter</strong>.
                    It wraps the legacy object and exposes the interface that your modern system expects.
                    It translates calls on the fly.
                </p>

                {/* SVG Diagram */}
                <div className="bg-slate-50 border border-slate-200 rounded-xl p-6 flex flex-col items-center mt-6 dark:bg-slate-800 dark:border-slate-700">
                    <h3 className="sr-only">Adapter Pattern Diagram</h3>
                    <svg width="600" height="200" viewBox="0 0 600 200" className="max-w-full h-auto">
                        {/* Client */}
                        <rect x="50" y="50" width="120" height="100" fill="#f1f5f9" stroke="#94a3b8" rx="8" />
                        <text x="110" y="105" textAnchor="middle" className="font-bold fill-slate-700 text-sm">Client</text>

                        {/* Arrow */}
                        <path d="M 170 100 L 230 100" stroke="#cbd5e1" strokeWidth="2" markerEnd="url(#arrow)" />

                        {/* Interface */}
                        <rect x="230" y="50" width="120" height="100" fill="#fff" stroke="#3b82f6" strokeDasharray="4" rx="8" />
                        <text x="290" y="105" textAnchor="middle" className="font-bold fill-blue-600 text-sm">Target Interface</text>

                        {/* Arrow */}
                        <path d="M 350 100 L 410 100" stroke="#cbd5e1" strokeWidth="2" markerEnd="url(#arrow)" />

                        {/* Adapter */}
                        <rect x="410" y="50" width="120" height="100" fill="#eff6ff" stroke="#3b82f6" rx="8" />
                        <text x="470" y="105" textAnchor="middle" className="font-bold fill-blue-800 text-sm">Adapter</text>

                        <defs>
                            <marker id="arrow" markerWidth="10" markerHeight="10" refX="9" refY="3" orient="auto">
                                <path d="M0,0 L0,6 L9,3 z" fill="#cbd5e1" />
                            </marker>
                        </defs>
                    </svg>
                    <p className="text-xs text-slate-400 mt-2 italic">The Client talks to the Adapter via the Target Interface.</p>
                </div>
            </PatternSection>

            <PatternSection title="Interactive Playground">
                <AdapterDemo />
            </PatternSection>

            <PatternSection title="Implementation Details">
                <CodeTabs
                    tabs={[
                        {
                            id: "logic",
                            label: "adapter.ts",
                            language: "typescript",
                            icon: <Box size={14} />,
                            code: `// 1. The Target Interface that our App expects
interface ChartPoint {
  x: string;   // Label
  y: number;   // Value
}

// 2. The Legacy Data (Source)
interface LegacyXML {
  _content: string; // "<stock><val>100</val></stock>"
}

// 3. The Adapter
class XMLToChartAdapter {
  constructor(private legacyService: LegacyService) {}

  async fetchChartData(): Promise<ChartPoint[]> {
    // A. Fetch raw data
    const xml = await this.legacyService.getXML();
    
    // B. Adapt / Transform logic
    const value = parseXML(xml._content).stock.val;
    
    // C. Return standard format
    return [{ x: "Stock A", y: parseInt(value) }];
  }
}`
                        },
                        {
                            id: "usage",
                            label: "Dashboard.tsx",
                            language: "tsx",
                            icon: <Layout size={14} />,
                            code: `// The UI component stays clean!
const Dashboard = () => {
  const [data, setData] = useState<ChartPoint[]>([]);

  useEffect(() => {
    // We use the adapter, but the component doesn't know!
    // It just thinks it's calling a standard service.
    const adapter = new XMLToChartAdapter(legacyService);
    
    adapter.fetchChartData().then(setData);
  }, []);

  return <BarChart data={data} />;
};`
                        },
                        {
                            id: "test",
                            label: "adapter.test.ts",
                            language: "typescript",
                            icon: <TestTube size={14} />,
                            code: `test("Adapter transforms XML to JSON correctly", async () => {
  const mockLegacy = { getXML: () => ({ _content: "<val>50</val>" }) };
  const adapter = new XMLToChartAdapter(mockLegacy);

  const result = await adapter.fetchChartData();

  expect(result).toEqual([{ x: "Stock A", y: 50 }]);
});`
                        }
                    ]}
                />
            </PatternSection>

            {content && (
                <PatternSection title="Pros & Cons">
                    <ProsConsList pros={content.pros} cons={content.cons} />
                </PatternSection>
            )}

            <PatternSection title="In Next.js Context">
                <p>
                    Next.js <strong>Route Handlers</strong> often act as Adapters. They take extensive HTTP Requests (Headers, Cookies, Body) and "adapt" them into simple arguments for your core business logic or database functions.
                </p>
            </PatternSection>
        </div>
    );
}

import React from "react";
import { Metadata } from "next";
import { BuilderDemo } from "@/components/features/builder/BuilderDemo";
import { CodeTabs } from "@/components/shared/CodeTabs";
import { patterns } from "@/lib/registry";
import { prosConsData } from "@/lib/pattern-content";
import { PatternHeader } from "@/components/shared/pattern-layout/PatternHeader";
import { PatternSection } from "@/components/shared/pattern-layout/PatternSection";
import { ProsConsList } from "@/components/shared/pattern-layout/ProsConsList";

export const dynamic = "force-static";
export const metadata: Metadata = { title: "Builder Pattern", description: "Learn the Builder Pattern." };
const pattern = patterns.find((p) => p.slug === "builder");
const content = prosConsData["builder"];

export default function BuilderPage() {
  if (!pattern) return <div>Pattern not found</div>;

  return (
    <div className="max-w-4xl mx-auto px-6 py-12 space-y-12">
      <PatternHeader
        title={pattern.title}
        description={pattern.description}
        category={pattern.category}
        tags={pattern.tags}
      />

      <PatternSection title="The Problem">
        <p>
          Imagine a complex object that requires laborious, step-by-step initialization of many fields and nested objects. Such initialization code is usually buried inside a monstrous constructor with lots of parameters.
        </p>
        <p>
          For example, think about creating a <code>House</code> object. To build a simple house, you need to construct four walls, a floor, and a roof. But what if you want a bigger house with a pool and fancy statues? You'd end up with a constructor like <code>new House(true, true, false, true, 4, "swimming pool", "gold")</code>.
        </p>
      </PatternSection>

      <PatternSection title="The Solution">
        <p>
          The <strong>Builder pattern</strong> suggests that you extract the object construction code out of its own class and move it to separate objects called <em>builders</em>.
        </p>
        <p>
          The pattern organizes object construction into a set of steps (<code>buildWalls</code>, <code>buildDoor</code>, etc.). To create an object, you execute a series of these steps on a builder object.
        </p>
      </PatternSection>

      <PatternSection title="Interactive Playground">
        <BuilderDemo />
      </PatternSection>

      <PatternSection title="Implementation Details">
        <CodeTabs
          tabs={[
            {
              id: "ts",
              label: "QueryBuilder.ts",
              language: "typescript",
              code: `class QueryBuilder {
  private query: Partial<Query> = {};

  select(fields: string): this {
    this.query.select = fields;
    return this;
  }

  from(table: string): this {
    this.query.table = table;
    return this;
  }

  where(condition: string): this {
    this.query.where = condition;
    return this;
  }

  build(): string {
    return \`SELECT \${this.query.select} FROM \${this.query.table} WHERE \${this.query.where}\`;
  }
}`
            },
            {
              id: "usage",
              label: "usage.ts",
              language: "typescript",
              code: `const query = new QueryBuilder()
  .select("id, name")
  .from("users")
  .where("active = true")
  .build();

console.log(query);`
            }
          ]}
        />
      </PatternSection>

      {content && (
        <PatternSection title="Pros & Cons">
          <ProsConsList pros={content.pros} cons={content.cons} />
        </PatternSection>
      )}
    </div>
  );
}

import React from "react";
import { Metadata } from "next";
import { ChainDemo } from "@/components/features/chain-of-responsibility/ChainDemo";
import { CodeTabs } from "@/components/shared/CodeTabs";
import { patterns } from "@/lib/registry";
import { prosConsData } from "@/lib/pattern-content";
import { PatternHeader } from "@/components/shared/pattern-layout/PatternHeader";
import { PatternSection } from "@/components/shared/pattern-layout/PatternSection";
import { ProsConsList } from "@/components/shared/pattern-layout/ProsConsList";

export const dynamic = "force-static";
export const metadata: Metadata = { title: "Chain of Responsibility Pattern", description: "Learn the Chain of Responsibility Pattern." };
const pattern = patterns.find((p) => p.slug === "chain-of-responsibility");
const content = prosConsData["chain-of-responsibility"];

export default function ChainPage() {
    if (!pattern) return <div>Pattern not found</div>;

    return (
        <div className="max-w-4xl mx-auto px-6 py-12 space-y-12">
            <PatternHeader
                title={pattern.title}
                description={pattern.description}
                category={pattern.category}
                tags={pattern.tags}
            />

            <PatternSection title="The Problem">
                <p>
                    Imagine that you are building an online ordering system. You want to restrict access to the system so only authenticated users can create orders. Also, users who have administrative permissions must have full access to all orders.
                </p>
                <p>
                    After a while, you realized that these checks must be performed sequentially. The application attempts to authenticate a user to the system whenever it receives a request that contains the user's credentials. However, if those credentials aren't correct and authentication fails, there's no reason to proceed with any other checks.
                </p>
            </PatternSection>

            <PatternSection title="The Solution">
                <p>
                    The <strong>Chain of Responsibility</strong> relies on transforming particular behaviors into stand-alone objects called <em>handlers</em>. In our case, each check should be extracted to its own class with a single method that performs the check.
                </p>
                <p>
                    The request travels along the chain until all handlers have had a chance to process it. Here's the best part: a handler can decide not to pass the request further down the chain and effectively stop any further processing.
                </p>
            </PatternSection>

            <PatternSection title="Interactive Playground">
                <ChainDemo />
            </PatternSection>

            <PatternSection title="Implementation Details">
                <CodeTabs tabs={[{
                    id: "ts", label: "Example", language: "typescript", code: `interface Handler {
  setNext(handler: Handler): Handler;
  handle(request: string): string | null;
}

abstract class AbstractHandler implements Handler {
  private nextHandler: Handler | null = null;

  public setNext(handler: Handler): Handler {
    this.nextHandler = handler;
    return handler;
  }

  public handle(request: string): string | null {
    if (this.nextHandler) {
      return this.nextHandler.handle(request);
    }
    return null;
  }
}` }]} />
            </PatternSection>

            {content && (
                <PatternSection title="Pros & Cons">
                    <ProsConsList pros={content.pros} cons={content.cons} />
                </PatternSection>
            )}
        </div>
    );
}

import React from "react";
import { Metadata } from "next";
import { CommandDemo } from "@/components/features/command/CommandDemo";
import { CodeTabs } from "@/components/shared/CodeTabs";
import { patterns } from "@/lib/registry";
import { prosConsData } from "@/lib/pattern-content";
import { PatternHeader } from "@/components/shared/pattern-layout/PatternHeader";
import { PatternSection } from "@/components/shared/pattern-layout/PatternSection";
import { ProsConsList } from "@/components/shared/pattern-layout/ProsConsList";

export const dynamic = "force-static";
export const metadata: Metadata = { title: "Command Pattern", description: "Learn the Command Pattern." };
const pattern = patterns.find((p) => p.slug === "command");
const content = prosConsData["command"];

export default function CommandPage() {
    if (!pattern) return <div>Pattern not found</div>;

    return (
        <div className="max-w-4xl mx-auto px-6 py-12 space-y-12">
            <PatternHeader
                title={pattern.title}
                description={pattern.description}
                category={pattern.category}
                tags={pattern.tags}
            />

            <PatternSection title="The Problem">
                <p>
                    Imagine you are building a text-editor app. You create a toolbar with a bunch of buttons for various operations. You create a <code>Button</code> class that can be used for toolbar buttons, as well as for generic buttons in various dialogs.
                </p>
                <p>
                    While all of these buttons look similar, they do different things. Where should you put the code for the various click handlers of these buttons? The simplest solution is to create tons of subclasses for each place where the button is used. This is bad.
                </p>
            </PatternSection>

            <PatternSection title="The Solution">
                <p>
                    The <strong>Command pattern</strong> suggests that GUI objects shouldn't send these requests directly. Instead, you should extract all of the request details, such as the object being called, the name of the method and the list of arguments into a separate <em>command</em> class with a single method that triggers this request.
                </p>
            </PatternSection>

            <PatternSection title="Interactive Playground">
                <CommandDemo />
            </PatternSection>

            <PatternSection title="Implementation Details">
                <CodeTabs tabs={[{
                    id: "ts", label: "Example", language: "typescript", code: `interface Command {
  execute(): void;
  undo(): void;
}

class CopyCommand implements Command {
  constructor(private editor: Editor) {}

  execute() {
    this.editor.copySelection();
  }

  undo() {
     // no-op
  }
}

class Button {
  constructor(private command: Command) {}

  click() {
    this.command.execute();
  }
}` }]} />
            </PatternSection>

            {content && (
                <PatternSection title="Pros & Cons">
                    <ProsConsList pros={content.pros} cons={content.cons} />
                </PatternSection>
            )}
        </div>
    );
}

import React from "react";
import { Metadata } from "next";
import { CompositeDemo } from "@/components/features/composite/CompositeDemo";
import { CodeTabs } from "@/components/shared/CodeTabs";
import { patterns } from "@/lib/registry";
import { prosConsData } from "@/lib/pattern-content";
import { PatternHeader } from "@/components/shared/pattern-layout/PatternHeader";
import { PatternSection } from "@/components/shared/pattern-layout/PatternSection";
import { ProsConsList } from "@/components/shared/pattern-layout/ProsConsList";

export const dynamic = "force-static";
export const metadata: Metadata = { title: "Composite Pattern", description: "Learn the Composite Pattern." };
const pattern = patterns.find((p) => p.slug === "composite");
const content = prosConsData["composite"];

export default function CompositePage() {
    if (!pattern) return <div>Pattern not found</div>;

    return (
        <div className="max-w-4xl mx-auto px-6 py-12 space-y-12">
            <PatternHeader
                title={pattern.title}
                description={pattern.description}
                category={pattern.category}
                tags={pattern.tags}
            />

            <PatternSection title="The Problem">
                <p>
                    Using the Composite pattern makes sense only when the core model of your app can be represented as a tree.
                </p>
                <p>
                    For example, imagine that you have two types of objects: <code>Products</code> and <code>Boxes</code>. A Box can contain several Products as well as a number of smaller Boxes. These little Boxes can also hold some Products or even smaller Boxes, and so on.
                </p>
            </PatternSection>

            <PatternSection title="The Solution">
                <p>
                    The <strong>Composite pattern</strong> suggests that you work with Products and Boxes through a common interface which declares a method for calculating the total price.
                </p>
                <p>
                    For a product, it's simply the price. For a box, it goes through each item the box contains, asks its price and then returns a total for this box. If one of these items were a smaller box, that box would also start going over its contents and so on, until the prices of all inner components are calculated.
                </p>
            </PatternSection>

            <PatternSection title="Interactive Playground">
                <CompositeDemo />
            </PatternSection>

            <PatternSection title="Implementation Details">
                <CodeTabs tabs={[{
                    id: "ts", label: "Example", language: "typescript", code: `interface Component {
  getWeight(): number;
}

class Product implements Component {
  constructor(private weight: number) {}
  getWeight() { return this.weight; }
}

class Box implements Component {
  private children: Component[] = [];

  add(c: Component) { this.children.push(c); }
  
  getWeight() {
    return this.children.reduce((sum, c) => sum + c.getWeight(), 0);
  }
}` }]} />
            </PatternSection>

            {content && (
                <PatternSection title="Pros & Cons">
                    <ProsConsList pros={content.pros} cons={content.cons} />
                </PatternSection>
            )}
        </div>
    );
}

import React from "react";
import { Metadata } from "next";
import { MediatorDemo } from "@/components/features/mediator/MediatorDemo";
import { CodeTabs } from "@/components/shared/CodeTabs";
import { patterns } from "@/lib/registry";
import { prosConsData } from "@/lib/pattern-content";
import { PatternHeader } from "@/components/shared/pattern-layout/PatternHeader";
import { PatternSection } from "@/components/shared/pattern-layout/PatternSection";
import { ProsConsList } from "@/components/shared/pattern-layout/ProsConsList";

export const dynamic = "force-static";
export const metadata: Metadata = { title: "Mediator Pattern", description: "Learn the Mediator Pattern." };
const pattern = patterns.find((p) => p.slug === "mediator");
const content = prosConsData["mediator"];

export default function MediatorPage() {
    if (!pattern) return <div>Pattern not found</div>;

    return (
        <div className="max-w-4xl mx-auto px-6 py-12 space-y-12">
            <PatternHeader
                title={pattern.title}
                description={pattern.description}
                category={pattern.category}
                tags={pattern.tags}
            />

            <PatternSection title="The Problem">
                <p>
                    Say you have a dialog for creating and editing customer profiles. It consists of various form controls such as text fields, checkboxes, buttons, etc.
                </p>
                <p>
                    Some of the form elements may interact with others. For instance, selecting the "I have a dog" checkbox may reveal a hidden text field for entering the dog's name. Another button might have to validate values of all fields before submitting.
                </p>
                <p>
                    If you implement this logic directly inside the code of the form elements (classes), you make them much harder to reuse.
                </p>
            </PatternSection>

            <PatternSection title="The Solution">
                <p>
                    The <strong>Mediator pattern</strong> suggests that you should cease all direct communication between the components which you want to make independent. Instead, these components must collaborate indirectly, by calling a special mediator object that redirects the calls to appropriate components.
                </p>
            </PatternSection>

            <PatternSection title="Interactive Playground">
                <MediatorDemo />
            </PatternSection>

            <PatternSection title="Implementation Details">
                <CodeTabs tabs={[{
                    id: "ts", label: "Example", language: "typescript", code: `interface Mediator {
  notify(sender: object, event: string): void;
}

class ConcreteMediator implements Mediator {
  private component1: Component1;
  private component2: Component2;

  constructor(c1: Component1, c2: Component2) {
    this.component1 = c1;
    this.component1.setMediator(this);
    this.component2 = c2;
